  },
  subtitle: {
    color: 'var(--text-secondary)',
    marginTop: 'var(--space-2)',
    fontSize: 'var(--font-size-sm)'
  },
  meta: {
    display: 'flex',
    gap: 'var(--space-4)',
    alignItems: 'flex-end',
  },
  metaItem: {
